<?PHPob_start();$path='../../../';$imagepath='../../';include_once "{$path}config/conf.php";include_once "../../pages/html_start.php";include_once "{$imagepath}checkadmin.php";if(!empty($_GET['directionrow'])){    if($_GET["directionrow"] === "asagi"){        $orderthanmore = prepare("`ordering`","`menyu`","`ordering`>?",[get('sira')],"`ordering`","ASC","LIMIT 1")->fetch(PDO::FETCH_ASSOC);        if (!empty($orderthanmore['ordering'])) {            update("`menyu`","`ordering`=?","`ordering`=?",[get('sira'),$orderthanmore['ordering']]);            update("`menyu`","`ordering`=?","`id`=?",[$orderthanmore['ordering'],get('id')]);        }    }elseif($_GET["directionrow"] === "yuxari"){        $orderthanlittle = prepare("`ordering`","`menyu`","`ordering`<?",[get('sira')],"`ordering`","DESC","LIMIT 1")->fetch(PDO::FETCH_ASSOC);        if (!empty($orderthanlittle['ordering'])) {            update("`menyu`","`ordering`=?","`ordering`=?",[get('sira'),$orderthanlittle['ordering']]);            update("`menyu`","`ordering`=?","`id`=?",[$orderthanlittle['ordering'],get('id')]);        }    }}if ($_SERVER['REQUEST_METHOD'] === 'POST'){    if(!empty($_POST['check_list'])) {        foreach($_POST['check_list'] as $check) {            if(isset($_POST['delete'])) {                delete("`menyu`","`id`=?",[$check]);            }            if(isset($_POST['doactive'])){                update("`menyu`","`status`=?","`id`=?",['1',$check]);            }            if(isset($_POST['dodeactive'])){                update("`menyu`","`status`=?","`id`=?",['1',$check]);            }        }    }    if (!empty(post('searchtext'))){        $where = "`name` LIKE ?";        $execute = ["%".post('searchtext')."%"];    }else{        $where = "`id`>?";        $execute = ["0"];    }    if (empty(post('rowsize'))){$rowsize = "10";}else{$rowsize = post('rowsize');}    if (isset($_GET["pagination"])) {$page = intval($_GET["pagination"]);} else {$page = 1;};    $start_from = ($page > 1) ? ($page * $rowsize) - $rowsize : 0;    $pagination = pagination($rowsize,$page,"2","menyu","`id`>?",["0"],"menyu");    $menyufetch = prepare("*","`menyu`",$where,$execute,"`ordering`","ASC","LIMIT $start_from,$rowsize");}else{    if (isset($_GET["pagination"])) {$page = intval($_GET["pagination"]);} else {$page = 1;}    $perpage = 10;    $start_from = ($page > 1) ? ($page * $perpage) - $perpage : 0;    $pagination = pagination($perpage,$page,"2","menyu","`id`>?",["0"],"menyu");    $menyufetch = select("*","`menyu`","`ordering`","ASC","LIMIT $start_from,$perpage");}?>    <div id="wrapper">        <?PHP include '../../nav.php';?>        <div id="page-wrapper">            <div class="row">                <div class="col-lg-12">					<h1 class="page-header">Menyular</h1>					<form action="" method="POST">						<table class="table table-bordered table-hover sortable-theme-bootstrap" data-sortable>							<thead>								<tr>									<th><input type="checkbox" id="checkAll"></th>									<th class="textcenter">Id</th>									<th class="textcenter">Ad</th>									<th class="textcenter">Link Adı</th>									<th class="textcenter">Yerləşməsi</th>									<th class="textcenter">Ana kateqoriyası</th>									<th class="textcenter">Sıra</th>									<th class="textcenter">Status</th>									<th class="textcenter">Dəyiş</th>								</tr>							</thead>							<tbody>                                <?PHP                                    while ($menyu = fetch($menyufetch)){                                        $parentmenyu = prepare("`name`","`menyu`","`id`=?",[$menyu['parent_id']])->fetch();                                        if($menyu['parent_id']!=0){                                            $level='<span class="glyphicon glyphicon-level-up"></span>  ';                                        }else{      