                                <? endif; ?>                                    <td class="textcenter">                                    <? if($menyu['status']==1):?>                                        <span class="green glyphicon glyphicon-eye-open"></span>                                    <? else: ?>                                        <span class="red glyphicon glyphicon-eye-close"></span>                                    <? endif;?>                                    </td>                                    <td style="width:112px;">                                        <a href="menyu/edit/<?=$menyu['id']?>/">                                            <span class="glyphicon glyphicon-edit"></span>                                        </a>                                        <a class="delete-item" onclick="$.deleteAjax($(this))" data-id="<?=$menyu['id']?>" data-value="menyu" data-url="<?=URL?>">                                            <span class="glyphicon glyphicon-remove fr red"></span>                                        </a>                                        <a href="menyu/show/<?=$menyu['id']?>/">                                            <span class="glyphicon glyphicon-eye-open" style="position: relative;left: 25px;"></span>                                        </a>                                    </td>                                </tr>                                <? }  ?>								<div class="row">									<div class="mb10">										<div class="col-md-4">											<a type="button" href="menyu/create/" class="btn btn-primary">Əlavə Et</a>											<button type="submit" name="delete" class="btn btn-danger">Sil</button>											<button type="submit" name="doactive" class="btn btn-success">Aktiv Et</button>											<button type="submit" name="dodeactive" class="btn btn-danger">Deaktiv Et</button>										</div>                                       